function goToByScroll(o){$("html,body").animate({scrollTop:$("#page_"+o).offset().top},"slow")}function zoomout(){var o=$("img.lazy").width(),i=$("img.lazy").height();$("img.lazy").width(.9*o),$("img.lazy").height(.9*i),$(".imgcontainer").css({width:.9*o,height:.9*i})}function zoomin(){var o=$("img.lazy").width(),i=$("img.lazy").height();$("img.lazy").width(1.1*o),$("img.lazy").height(1.1*i),$(".imgcontainer").css({width:1.1*o,height:1.1*i})}function highlightborder(o){$("#"+o).css({borderTopColor:"blue",borderBottomColor:"blue",borderRightColor:"blue",borderLeftColor:"blue",borderBottomWidth:2}),$("#"+o).siblings().css({borderTopColor:"",borderBottomColor:"",borderRightColor:"",borderLeftColor:"",borderBottomWidth:1}),$("#"+o).addClass("selectedpage"),$("#"+o).siblings.removeClass("selectedpage")}$("img.lazy").lazyload({threshold:600,skip_invisibles:!1}),$(".thumbnail").live("click",function(){goToByScroll($(this).attr("id")),highlightborder($(this).attr("id"))}),$("#zoomin").live("click",function(){zoomin()}),$("#zoomout").live("click",function(){zoomout()});